import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Alert } from 'react-native';

export default function ManualFoodLoggingScreen() {
  const [food, setFood] = useState('');
  const [quantity, setQuantity] = useState('');
  const [calories, setCalories] = useState(null);

  const calculateCalories = () => {
    const total = parseFloat(quantity) * 100;
    setCalories(total);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Food Logging</Text>
      <TextInput style={styles.input} placeholder="Food Name" value={food} onChangeText={setFood} />
      <TextInput style={styles.input} placeholder="Quantity" keyboardType="numeric" value={quantity} onChangeText={setQuantity} />
      <Button title="Calculate Calories" onPress={calculateCalories} />
      {calories !== null && <Text>Estimated Calories: {calories} kcal</Text>}
    </View>
  );
}

const styles = StyleSheet.create({
